CREATE TABLE disposition (id INT AUTO_INCREMENT, text VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE location (id INT AUTO_INCREMENT, city_part TEXT, city TEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE property (id INT AUTO_INCREMENT, reference_no VARCHAR(255) NOT NULL, title TEXT NOT NULL, text TEXT NOT NULL, disposition_id INT NOT NULL, area FLOAT(10, 2) NOT NULL, floor INT NOT NULL, lift TINYINT(1) NOT NULL, cellar FLOAT(10, 2), balcony FLOAT(10, 2), location_id INT NOT NULL, price INT NOT NULL, created_on DATETIME NOT NULL, street TEXT NOT NULL, property_build_id INT NOT NULL, terace FLOAT(10, 2), loggia FLOAT(10, 2), garden FLOAT(10, 2), garage FLOAT(10, 2), parking_place TINYINT(1) DEFAULT '0' NOT NULL, INDEX disposition_id_idx (disposition_id), INDEX location_id_idx (location_id), INDEX property_build_id_idx (property_build_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE property_build (id INT AUTO_INCREMENT, text TEXT NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
ALTER TABLE property ADD CONSTRAINT property_property_build_id_property_build_id FOREIGN KEY (property_build_id) REFERENCES property_build(id);
ALTER TABLE property ADD CONSTRAINT property_location_id_location_id FOREIGN KEY (location_id) REFERENCES location(id);
ALTER TABLE property ADD CONSTRAINT property_disposition_id_disposition_id FOREIGN KEY (disposition_id) REFERENCES disposition(id);
